// Ingests data generated by a radar device
package main

import "fmt"

// Ingest a contact
func ingestContact(when int64, deviceUID string, deviceSN string, contactName string, contactAffiliation string, contactRole string, contactEmail string) (err error) {

	fmt.Printf("ingestContact: %s %d sn:'%s' name:'%s' affiliation:'%s' role:'%s' email:'%s'\n", deviceUID, when, deviceSN, contactName, contactAffiliation, contactRole, contactEmail)

	return
}

// Ingest a scan entry
func ingestScan(deviceUID string, scan RadarScan) (err error) {

	fmt.Printf("ingestScan: %s %+v\n", deviceUID, scan)

	return
}

// Ingest a track entry
func ingestTrack(deviceUID string, scan RadarTrack) (err error) {

	fmt.Printf("ingestTrack: %s %+v\n", deviceUID, scan)

	return
}
